tinymce.PluginManager.add('filemanager', function(editor) {
    function openmanager() {
        var win, data, dom = editor.dom,
            imgElm = editor.selection.getNode();
        var width, height, imageListCtrl;
        var title="RESPONSIVE FileManager";
        if (typeof tinymce.settings.filemanager_title !== "undefined" && tinymce.settings.filemanager_title) {
            title=tinymce.settings.filemanager_title;
        }
        win = editor.windowManager.open({
            title: title,
            file: tinyMCE.baseURL + '/plugins/filemanager/dialog.php?editor=' + editor.id + '&lang=' + tinymce.settings.language,
            filetype: 'all',
            classes: 'filemanager',
            width: 900,
            height: 600,
            inline: 1
        })
    }
    editor.addButton('filemanager', {
        icon: 'browse',
        tooltip: 'Insert file',
        onclick: openmanager,
        stateSelector: 'img:not([data-mce-object])'
    });
    editor.addMenuItem('filemanager', {
        icon: 'browse',
        text: 'Insert file',
        onclick: openmanager,
        context: 'insert',
        prependToContext: true
    })
});